import React,{Component} from 'react';
import Layout from '../components/Layout';
import web3 from '../web3.js';
import Lottery from '../ethereum/build/contracts/Lottery.json';
import Error from './_error.js'
import LotteryInstance from '../instance.js';
import {Form,Message,Button} from 'semantic-ui-react';
class App extends Component{
    state={
        errorMessage : '',
        enteredAmount:'',
        loading:false,
        players:[]
    }
    static async getInitialProps(){
        let errorOccured = false;
        let accounts,manager
        try{
            accounts = await web3.eth.getAccounts();
            manager = await LotteryInstance.methods.manager().call();
        }catch(err){
              console.log(err);
              errorOccured=true;
        }
        return {accounts,manager,errorOccured};
    }
    onSubmit = async (event) =>{
        this.setState({loading:true})
        try{
            let accounts = await web3.eth.getAccounts();
            await LotteryInstance.methods.enter()
            .send({from:accounts[0], value:web3.utils.toWei(this.state.enteredAmount,'ether')})
            console.log("success")
            let players = await LotteryInstance.methods.getPlayers().call()
            console.log("PLAYERS",players)
            this.setState({players})
        }catch(err){
            this.setState({errorMessage:err.message});        }
        this.setState({loading:false})
    }
    handleChange=(event)=>{
        this.setState({[event.target.name]:event.target.value});
    }
    render(){
        const {accounts,manager,errorOccured} = this.props;
        if(errorOccured){
            return <Error />
        }
        return(
            <Layout accounts={accounts}>
                <h2>Manager : {manager}</h2>
                <h3>Contract address : {LotteryInstance.options.address}</h3>
                <Form error={!!this.state.errorMessage} onSubmit={this.onSubmit}>
                    <Form.Field>
                        <Form.Input 
                        label='Enter Amount in Eth'
                        placeholder='Enter Ammount' 
                        value={this.state.enteredAmount}
                        name="enteredAmount"
                        onChange={this.handleChange}
                        />
                    </Form.Field>
                    <Message error header="Oops!" content={this.state.errorMessage} />
                    <Button loading={this.state.loading} color="green">Enter</Button>
                </Form>
                <h3>{this.state.players.length} Bids entered</h3>
            </Layout>

        )
    }
}

export default App;
