import React from 'react'
import LotteryInstance from '../instance';
class Enter extends React.Component{
    state={
        players:[],
        balanceInPool:''
    }
    async componentDidMount(){
        const players = await LotteryInstance.methods.getPlayers().call();
        const balanceInPool = await LotteryInstance.methods.getBalanceInPool().call();
        this.setState({palyers,balanceInPool})
    }
    render(){
        const {players,balanceInPool} = this.state
        return(
            <div>
                <h4>{players.length} bids are entered</h4>
                <h4>Blance in pool : {balanceInPool}</h4>
            </div>
        )
    }
}
export default Enter
